import React from 'react';
// import Button from 'react-bootstrap/Button'

function Shorten() {
    let api_url;
    function handleClick() {
        api_url = "https://api.shrtco.de/v2/"
        fetch(api_url)
            .then(res => res.json())
            .then(
                (result) => {
                    console.log(result, "result")
                    setInfo({
                        ipAddress: result.ip,
                        city: result.location.city,
                        region: result.location.region,
                        postalCode: result.location.postalCode,
                        lat: result.location.lat,
                        lng: result.location.lng,
                        timeZone: result.location.timezone,
                        isp: result.isp,
                    })
                },
                (error) => {
                    console.log(error, "error")
                }
            )

    }


    return (
        <div id="shorten-func-box">
            <input id="link-input" placeholder="shorten a link here..."></input>
            <button id="shorten-btn" onClick={handleClick}>Shorten It!</button>
        </div>
    )
}
export default Shorten;
