import { link } from 'fs';
import React, { useState } from 'react';
import { useEffect } from 'react';
// import Button from 'react-bootstrap/Button'

function Shorten() {
    let api_url = "https://api.shrtco.de/v2/shorten?url=";
    const [cardStatus, setCardStatus] = useState(false)
    const [linkInput, setLinkInput] = useState(null)
    const [linkShortten, setLinkShortten] = useState(null);
    const [resResult, setResResult] = useState(null)
    const [cardId, setCardId] = useState(null)
    const [n, setN] = useState(1);

    function handleClick() {
        let url = api_url + linkInput;
        console.log(url)
        fetch(url)
            .then(res => res.json())
            .then(
                (result) => {
                    console.log("resResult", result)
                    // setResResult(result.result.full_short_link)

                    if (n === 1) {
                        document.getElementById("card-container-id-1").classList.remove("imgDisplay");
                        setCardStatus(true);
                        setLinkShortten(linkInput);
                        setResResult(result.result.full_short_link)
                        setCardId("card-container-id-1")
                        setN(2)

                    } else {
                        console.log("bigger than 1", n)
                        let card = document.getElementById("card-container-id-1")
                        let cardCloned = card.cloneNode(true)
                        cardCloned.id = "card-container-id-" + (n);
                        setCardId(cardCloned.id);
                        document.getElementById("result-cards-container").append(cardCloned)
                        setN(n + 1)
                        setLinkShortten(linkInput);
                        setResResult(result.result.full_short_link)
                        console.log("get result", result.result.full_short_link)
                        // cardClone();

                    }
                },
                (error) => {
                    console.log(error, "error")
                }
            )
    }


    if (localStorage.length === 0 && linkShortten !== null && cardId !== null) {
        console.log("in if statement")
        let dataStorage = {
            input: linkShortten,
            res: resResult
        }
        localStorage.setItem(cardId, JSON.stringify(dataStorage))

        console.log("local", localStorage)
    }

    useEffect(() => {
        console.log("localstorage", localStorage)
        if (localStorage.length > 0) {
            var initalIndex = localStorage.length;
            setN(initalIndex)
            console.log(initalIndex)


            var keys = Object.keys(localStorage)
            keys.map((key) => {
                console.log(key)
                let dataStorage = JSON.parse(localStorage.getItem(key))

                if (key === "card-container-id-1") {
                    document.getElementById("card-container-id-1").classList.remove("imgDisplay");
                    setLinkShortten(dataStorage.input);
                    setResResult(dataStorage.res)
                    console.log("in 1")
                } else {
                    console.log("in2")
                    let card = document.getElementById("card-container-id-1")
                    let cardCloned = card.cloneNode(true)
                    cardCloned.id = key;
                    setCardId(cardCloned.id);
                    document.getElementById("result-cards-container").append(cardCloned)
                    setN(n + 1)
                    setLinkShortten(dataStorage.input);
                    setResResult(dataStorage.res)

                }
                // document.getElementById(key)
                // setLinkShortten(dataStorage.input);
                // setResResult(dataStorage.res)
            })
        } else if (localStorage.length === 0) {
            setN(1)
        }





        // document.getElementById("card-container-id-1").classList.remove("imgDisplay")
        // setLinkShortten();
        // setResResult(result.result.full_short_link)

    }

        // document.addEventListener('storage', checkUserData)

        , [])

    // localStorage.clear()













    return (
        <div id="shorten-link-box">
            <div id="shorten-func-box">
                <input id="link-input" placeholder="shorten a link here..." onChange={(e) => { setLinkInput(e.target.value) }}></input>
                <button className="shorten-btn" onClick={handleClick}>Shorten It!</button>
            </div>
            <div id="result-cards-container">
                <div id="card-container-id-1" className="card-container imgDisplay" >
                    <div className="result-card">
                        <p className="result-card-text card-input">{linkShortten}</p>
                        <hr></hr>
                        <p className="result-card-text card-res">{resResult}</p>
                        <button className="shorten-btn" onClick={() => { navigator.clipboard.writeText(resResult) }}> Copy </button>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default Shorten;
