                        setCardId("card-container-id-1")
                        console.log("get result", result.result.full_short_link)

                        // let dataStorage = {
                        //     input: linkInput,
                        //     res: result.result.full_short_link
                        // }
                        // localStorage.setItem("card" + n, JSON.stringify(dataStorage))

                        // console.log("datastorage", dataStorage)
                        // console.log("localstorage", localStorage)


                    } else {
                        console.log("cardsatus", cardStatus, n)
                        let card = document.getElementById("card-container-id-" + n)
                        let cardCloned = card.cloneNode(true)
                        cardCloned.id = "card-container-id-" + (n + 1);
                        setCardId(cardCloned.id);
                        console.log("cardCloned", cardCloned.id)
                        document.getElementById("result-cards-container").append(cardCloned)
                        setN(n + 1)
                        setLinkShortten(linkInput);
                        setResResult(result.result.full_short_link)
                        console.log("get result", result.result.full_short_link)

                        // let dataStorage = {
                        //     input: linkInput,
                        //     res: result.result.full_short_link
                        // }
                        // localStorage.setItem("card" + n, JSON.stringify(dataStorage))

                        // console.log("datastorage", dataStorage)
                        // console.log("localstorage", localStorage)

                    }
                },
                (error) => {
                    console.log(error, "error")
                }
            )
    }

    if (linkShortten !== null && cardId !== null) {
        console.log("in if statement")
        let dataStorage = {
            input: linkShortten,
            res: resResult
        }
        localStorage.setItem(cardId, JSON.stringify(dataStorage))

        console.log("local", localStorage)
    }

    useEffect(() => {
        // console.log("inuseeffect")
