import { link } from 'fs';
import React, { useState } from 'react';
import { useEffect } from 'react';
// import Button from 'react-bootstrap/Button'

function Shorten() {
    let api_url = "https://api.shrtco.de/v2/shorten?url=";
    const [storageStatus, setStorageStatus] = useState(false) ///if user has used the app before? false - no, true - ye
    const [linkInput, setLinkInput] = useState(null)

    const [LinkToBeShortten, setLinkToBeShortten] = useState(null);
    const [resResult, setResResult] = useState(null)
    const [n, setN] = useState(1);
    useEffect(() => {
        if (localStorage.length !== 0) {
            setN(localStorage.length + 1)
        }
    }, [])



    async function handleClick() {
        console.log("*********start handle click**********")
        await fetchData();
        console.log("********finished handle Click*******")



    }

    async function fetchData() {
        let url = api_url + linkInput;
        setStorageStatus(true);
        await fetch(url)
            .then(res => res.json())
            .then(
                (data) => {
                    console.log("1. getting data", data)
                    showCards(data);
                    updateToStorage(data)
                },
                (error) => {
                    console.log(error, "error")
                }
            )
    }
    async function showCards(data) {

        var shorttedLink, userInput;
        shorttedLink = data.result.full_short_link
        userInput = linkInput

        console.log("received data, put to card", shorttedLink, userInput)

        if (localStorage.length === 0 && !storageStatus) { // first time input
            document.querySelector(".card-container").classList.remove("imgDisplay");
            setStorageStatus(true);
            setN(n + 1)
            setLinkToBeShortten(linkInput);
            setResResult(shorttedLink)

            console.log("finish intial render")

        }
        else if (localStorage.length !== 0 && storageStatus) {//second time input 
            let card = document.querySelector(".card-container")
            let cardCloned = card.cloneNode(true)
            cardCloned.id = "card-container-id-" + n;
            document.getElementById("result-cards-container").append(cardCloned)
            setN(n + 1)
            setLinkToBeShortten(linkInput);
            setResResult(shorttedLink)
            console.log("after intial render, cardCloned.id", cardCloned.id)
        }
        console.log("2. show cards", LinkToBeShortten, resResult)
    }

    async function updateToStorage(data) {

        let dataStorage = {
            input: data.result.original_link,
            res: data.result.full_short_link
        }

        var cardId = "card-container-id-" + n
        console.log("carID,", n)

        localStorage.setItem(cardId, JSON.stringify(dataStorage))
        console.log("3. update to datastorage", dataStorage, localStorage, cardId)
    }


    console.log("*render*")
    console.log("in render", LinkToBeShortten, resResult)
    console.log("localstorage", localStorage)
    console.log("n", n)
    return (
        <div id="shorten-link-box">
            <div id="shorten-func-box">
                <input id="link-input" placeholder="shorten a link here..." onChange={(e) => { setLinkInput(e.target.value) }}></input>
                <button className="shorten-btn" onClick={handleClick}>Shorten It!</button>
            </div>
            <div id="result-cards-container">
                {localStorage.length == 0 ?
                    <div id="card-container-id-1" className="card-container imgDisplay" >
                        <div className="result-card">
                            <p className="result-card-text card-input">{LinkToBeShortten}</p>
                            <hr></hr>
                            <p className="result-card-text card-res">{resResult}</p>
                            <button className="shorten-btn" onClick={() => { navigator.clipboard.writeText(resResult) }}> Copy </button>
                        </div>
                    </div>

                    : Object.keys(localStorage).map((key) => {
                        { var linkShortten = JSON.parse(localStorage.getItem(key)).input }
                        { var resReceived = JSON.parse(localStorage.getItem(key)).res }
                        return (
                            < div id={key} className="card-container" >
                                <div className="result-card">
                                    <p className="result-card-text card-input">{linkShortten}</p>
                                    {/* {console.log("in render", JSON.parse(localStorage.getItem(key)).input)} */}
                                    <hr></hr>
                                    <p className="result-card-text card-res">{resReceived}</p>
                                    <button className="shorten-btn" onClick={() => { navigator.clipboard.writeText(resReceived) }}> Copy </button>
                                </div>
                            </div>
                        )

                    })




                }

            </div>
        </div >
    )
}
export default Shorten;
